<?php	class BerthViewModel extends ViewModel {        protected $viewFields;		public function _initialize(){			$main_must_field = array(                'berth_id',                'owner_role_id',                'creator_role_id',                'delete_role_id',                'create_time',                'delete_time',                'update_time',                'is_deleted',                'league_id',                'name'=>'berth_name'            );			$main_list = array_unique(array_merge(M('Fields')->where(array('model'=>'berth','is_main'=>1))->getField('field', true),$main_must_field));            $main_list['_type'] = 'LEFT';            $this->viewFields = array(                'berth'=>$main_list,                'dorm'=>array(                    'dorm_id',                    'dorm_id',                    'name'=>'dorm_name',                    'idcode'=>'dorm_idcode',                    '_on'=>'berth.dorm_id = dorm.dorm_id',                    '_type'=>'LEFT'                ),                'product'=>array(                    'product_id',                    'name'=>'product_name',                    'idcode'=>'product_idcode',                    '_on'=>'berth.product_id = product.product_id',                    '_type'=>'LEFT'                ),            );		}        public function verity_check($berth, $cverity = true) {            $new_berth = $this->where('berth.berth_id = %d',$berth['berth_id'])->find();            $field_list = M('Fields')->where('model = "berth"')->order('order_id')->select();            $exclude_fields = array(                "create_time",                "update_time",            );            $change_fields = array();            foreach($field_list as $fv) {                if (in_array($fv['field'], $exclude_fields)) {                    continue;                }                if ($new_berth[$fv['field']] != $berth[$fv['field']]) {                    $change_fields[] = model_field_diff($fv, $new_berth, $berth);                }            }            if ($cverity) {                $change_assort = array("basic" => 0);                foreach ($change_fields as $fv) {                    if ($fv['in_verify'] == 0) {                        continue;                    }                    $assort = "basic";                    $field_group_id = $fv['field_group_id'];                    if ($field_group_id != 0) {                        $field_group = M('fields_group')->where(array('field_group_id' => $field_group_id))->find();                        if ($field_group) {                            $assort = $field_group['assort'];                        }                    }                    $change_assort[$assort] += 1;                }                $where = array(                    "berth_id" => $berth['berth_id']                );                $m_berth = D('berth');                if ($change_assort['basic'] > 0) {                    $m_berth->where($where)->setField("basic_verify", 0);                }                $m_berth->where($where)->setField("is_verify", 1);            }            return $change_fields;        }	}