<?php	class CultivateViewModel extends ViewModel {        protected $viewFields;		public function _initialize(){			$main_must_field = array(                'cultivate_id',                'owner_role_id',                'creator_role_id',                'create_time',                'update_time',                'model'=>"corre",                'model',                'model_id',                'idcode',                'league_id',                'channel_role_model_keyword',                'channel_role_id_keyword',                'idcode'=>'cultivate_idcode',            );			$main_list = array_unique(array_merge(M('Fields')->where(array('model'=>'cultivate','is_main'=>1))->getField('field', true),$main_must_field));            $main_list['_type'] = 'LEFT';            $this->viewFields = array(                'cultivate'=>$main_list,                'currier'=>array(                    'name'=>'currier_name',                    "idcode"=>'currier_idcode',                    "category_id"=>'category_id',                    '_on'=>'cultivate.currier_id=currier.currier_id',                    '_type'=>'LEFT'                )            );		}        public function verity_check($cultivate, $cverity = true) {            $new_cultivate = $this->where('cultivate.cultivate_id = %d',$cultivate['cultivate_id'])->find();            $field_list = M('Fields')->where('model = "cultivate"')->order('order_id')->select();            $exclude_fields = array(                "create_time",                "update_time",            );            $change_fields = array();            foreach($field_list as $fv) {                if (in_array($fv['field'], $exclude_fields)) {                    continue;                }                if ($new_cultivate[$fv['field']] != $cultivate[$fv['field']]) {                    $change_fields[] = model_field_diff($fv, $new_cultivate, $cultivate);                }            }            if ($cverity) {                $change_assort = array("basic" => 0);                foreach ($change_fields as $fv) {                    if ($fv['in_verify'] == 0) {                        continue;                    }                    $assort = "basic";                    $field_group_id = $fv['field_group_id'];                    if ($field_group_id != 0) {                        $field_group = M('fields_group')->where(array('field_group_id' => $field_group_id))->find();                        if ($field_group) {                            $assort = $field_group['assort'];                        }                    }                    $change_assort[$assort] += 1;                }                $where = array(                    "cultivate_id" => $cultivate['cultivate_id']                );                $m_cultivate = D('cultivate');                if ($change_assort['basic'] > 0) {                    $m_cultivate->where($where)->setField("basic_verify", 0);                }                $m_cultivate->where($where)->setField("is_verify", 1);            }            return $change_fields;        }	}