<?php	class CurrierViewModel extends ViewModel {        protected $viewFields;		public function _initialize(){			$main_must_field = array(                'currier_id',                'owner_role_id',                'creator_role_id',                'create_time',                'update_time',                'cultivate_used',                'league_id',                'name'=>'currier_name',            );			$main_list = array_unique(array_merge(M('Fields')->where(array('model'=>'currier','is_main'=>1))->getField('field', true),$main_must_field));            $main_list['_type'] = 'LEFT';            $this->viewFields = array(                'currier'=>$main_list,            );		}        public function verity_check($currier, $cverity = true) {            $new_currier = $this->where('currier.currier_id = %d',$currier['currier_id'])->find();            $field_list = M('Fields')->where('model = "currier"')->order('order_id')->select();            $exclude_fields = array(                "create_time",                "update_time",            );            $change_fields = array();            foreach($field_list as $fv) {                if (in_array($fv['field'], $exclude_fields)) {                    continue;                }                if ($new_currier[$fv['field']] != $currier[$fv['field']]) {                    $change_fields[] = model_field_diff($fv, $new_currier, $currier);                }            }            if ($cverity) {                $change_assort = array("basic" => 0);                foreach ($change_fields as $fv) {                    if ($fv['in_verify'] == 0) {                        continue;                    }                    $assort = "basic";                    $field_group_id = $fv['field_group_id'];                    if ($field_group_id != 0) {                        $field_group = M('fields_group')->where(array('field_group_id' => $field_group_id))->find();                        if ($field_group) {                            $assort = $field_group['assort'];                        }                    }                    $change_assort[$assort] += 1;                }                $where = array(                    "currier_id" => $currier['currier_id']                );                $m_currier = D('currier');                if ($change_assort['basic'] > 0) {                    $m_currier->where($where)->setField("basic_verify", 0);                }                $m_currier->where($where)->setField("is_verify", 1);            }            return $change_fields;        }	}